import React, { useState, useEffect, useRef } from "react";
import Unsplash, { toJson } from "unsplash-js";
import Loading from "./components/Loading/";
import Photos from "./Photos";

const unsplash = new Unsplash({
  accessKey: "8f9fbd10d8bb0a7e69dd531aea77d5a0b84152b806286ed7f83f896c1987413b",
});
export default function SearchPhotos() {

  const [isLoading, setIsLoading] = useState(false);
  const [query, setQuery] = useState("");
  const [pics, setPics] = useState([]);
  const [filteredPics, setFilteredPics] = useState([]);
  const [tags, setTags] = useState([]);
  const [filters, setFilters] = useState([]);
  const [targetElement, setTargetElement] = useState(null);
  const prevY = useRef(0); // storing the last intersection y position

  const options = {
    root: null,
    rootMargin: "0px",
    threshold: 1.0,
  };

  const handleObserver = (entities, observer) => {
    const y = entities[0].boundingClientRect.y;

    if (prevY.current > y) {
      searchPhotos();
    }

    prevY.current = y;
  };

  const observer = useRef(new IntersectionObserver(handleObserver, options));

  useEffect(() => {
    if (targetElement) {
      observer.current.observe(targetElement);
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [targetElement]);

  useEffect(() => {
    if (pics) {
      setTags([]);
      const tags = pics.map((pic) => {
        return pic.tags;
      });


      if (tags) {
        const titles = tags.map((tag) => {
          return tag.map((tg) => {
            return tg.title;
          });

        });

        if (titles) {
          titles.forEach((title) => {
            title.forEach((tt) => {
                setTags(tags => [...tags, tt]);
            });
          });

        }

      }
    }
  }, [pics]);

  useEffect(() => {
    const uniqueTags = tags.filter(function(elem, pos) {
        return tags.indexOf(elem) === pos;
    });

    setFilters(uniqueTags);
  }, [tags]);

  const searchPhotos = async (e) => {
    setIsLoading(true);
    setFilteredPics([]);
    e.preventDefault();

    unsplash.search
      .photos(query)
      .then(toJson)
      .then((json) => {
        setIsLoading(false);
        setPics(json.results);
      });

  };

  const handleChangeFilter = (e) => {
    const tag = e.target.value;
    const newPics = pics.filter((pic) => { return pic.tags.find((el) => el.title === tag) });
    setFilteredPics(newPics);
  }

  return (
    <>
      {isLoading ? (
        <Loading />
      ) : (
        <>
          <form className="form" onSubmit={searchPhotos}>
            {" "}
            <label className="label" htmlFor="query">
              {" "}
              ðŸ“·
            </label>
            <input
              type="text"
              name="query"
              className="input"
              placeholder={`Try "dog" or "apple"`}
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />
            <button type="submit" className="button">
              Search
            </button>
            {filters.length &&
              <>
                <span>Filter by Tags</span>
                <select onChange={handleChangeFilter}>
                  {filters.map((option) => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </>
            }

          </form>

            <Photos data={filteredPics.length ? filteredPics : pics} />
            <div
                className={"loading-new-images-container"}
                style={isLoading ? { display: "none" } : null}
                ref={setTargetElement}
              >
                <div className="loading-new-images">Loading New Images ...</div>
              </div>
        </>
      )}
    </>
  );
}
