import React, { useState, useEffect } from "react";
import Unsplash, { toJson } from "unsplash-js";
import Loading from "./components/Loading/";
import Photos from "./Photos";

const unsplash = new Unsplash({
  accessKey: "8f9fbd10d8bb0a7e69dd531aea77d5a0b84152b806286ed7f83f896c1987413b",
});

const PaginationBar = ({
  handlePrevPageCall,
  currentPage,
  handleNextPageCall,
}) => (
  <div className="btn-container">
    <Button onClick={handlePrevPageCall}>Previous Page</Button>
    <div className={"current-page"}>Page {currentPage}</div>
    <Button onClick={handleNextPageCall}>Next Page</Button>
  </div>
);

export default function SearchPhotos() {

  const [isLoading, setIsLoading] = useState(false);
  const [query, setQuery] = useState("");
  const [pics, setPics] = useState([]);
  const [filteredPics, setFilteredPics] = useState([]);
  const [tags, setTags] = useState([]);
  const [filters, setFilters] = useState([]);

  useEffect(() => {
    if (pics) {
      setTags([]);
      const tags = pics.map((pic) => {
        return pic.tags;
      });


      if (tags) {
        const titles = tags.map((tag) => {
          return tag.map((tg) => {
            return tg.title;
          });

        });

        if (titles) {
          titles.forEach((title) => {
            title.forEach((tt) => {
                setTags(tags => [...tags, tt]);
            });
          });

        }

      }
    }
  }, [pics]);

  useEffect(() => {
    const uniqueTags = tags.filter(function(elem, pos) {
        return tags.indexOf(elem) === pos;
    });

    setFilters(uniqueTags);
  }, [tags]);

  const searchPhotos = async () => {
    setIsLoading(true);
    setFilteredPics([]);
    // e.preventDefault();

    unsplash.search
      .photos(query)
      .then(toJson)
      .then((json) => {
        setIsLoading(false);
        setPics(json.results);
      });

  };

  const handleChangeFilter = (e) => {
    const tag = e.target.value;
    const newPics = pics.filter((pic) => { return pic.tags.find((el) => el.title === tag) });
    setFilteredPics(newPics);
  }

