            <label className="label" htmlFor="query">
              {" "}
              ðŸ“·
            </label>
            <input
              type="text"
              name="query"
              className="input"
              placeholder={`Try "dog" or "apple"`}
              value={query}
              onChange={(e) => setQuery(e.target.value)}
